package com.loopingstatement;

import java.util.Scanner;

public class ForLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int N=s.nextInt();
		for(int i=N;i>=1;i--)
		{
			System.out.println(i+"  ");
		}

	}

}
